mapper.select:
select s.id, s.`name`, s.`desc`, s.address, s.phone_number, s.image, s.priority, s.create_time, s.update_time, s.enable_status, s.advice, s.area_id, s.owner_id, s.shop_category_id, a.id , a.name aname, sc.id , sc.name scname from tb_shop s,tb_shop_category sc,tb_area a
and s.shop_category_id = #{shopCondition.shopCategory.id}
and s.shop_category_id in (select shop_category_id from tb_shop_category WHERE parent_id = #{shopCondition.shopCategory.parent.id})
and s.area_id = #{shopCondition.area.id}
and s.shop_name like '%${shopCondition.name}%'
and s.enable_status = #{shopCondition.enableStatus}
and s.owner_id = #{shopCondition.owner.id}
AND s.area_id=a.id AND s.shop_category_id = sc.id
ORDER BY s.priority DESC LIMIT #{rowIndex},#{pageSize};
